      background: @mantle;
    }

    /* bottom bar */
    ytm-pivot-bar-renderer {
      background: @mantle;
      color: @text;
      border-top-color: @surface0;
    }

    /* chips */
    .chip-bar {
      background-color: @base;
    }

    /* selected chip color */
    [chip-style="STYLE_DEFAULT"].selected .chip-container,
    [chip-style="STYLE_HOME_FILTER"].selected .chip-container,
    [chip-style="STYLE_COLOR_RED"].selected .chip-container,
    [chip-style="STYLE_COLOR_GREEN"].selected .chip-container,
    [chip-style="STYLE_COLOR_BLUE"].selected .chip-container {
